    <!--AJAX PARA CHAMAR O ENVIAR.PHP DO EMAIL -->
    <script type="text/javascript">
        $(document).ready(function() {

            $('#btn-enviar').click(function(event) {
                $('#mensagem').addClass('text-info')
                $('#mensagem').text("Enviando!!")
                event.preventDefault();

                $.ajax({
                    url: "enviar.php",
                    method: "post",
                    data: $('form').serialize(),
                    dataType: "text",
                    success: function(mensagem) {

                        $('#mensagem').removeClass()

                        if (mensagem.trim() === 'Enviado com Sucesso!') {

                            $('#mensagem').addClass('text-success')


                            $('#nome').val('');
                            $('#telefone').val('');
                            $('#email').val('');
                            $('#comentario').val('');

                            $('#mensagem').text(mensagem)
                            //$('#btn-fechar').click();
                            //location.reload();


                        } else {

                            $('#mensagem').addClass('text-danger')
                            $('#mensagem').text("VocÃª precisa estÃ¡ com o site hospedado para fazer envio de Emails")

                        }



                    },

                })
            })
        })
    </script>


